type Post {
  id: ID!
  title: String!
  body: String!
  published: Boolean!
  owner: User!
}

input CreatePostInput {
  title: String!
  body: String!
  published: Boolean!
  ownerId: String!
}

type User {
  id: ID!
  username: String!
  firstname: String!
  lastname: String!
  posts: [Post]
}

input CreateUserInput {
  username: String!
  firstname: String!
  lastname: String!
}

type Mutation {
  createUser(data: CreateUserInput!): User!
  createPost(data: CreatePostInput!): Post!
}

type Query {
  user(id: ID!): User!
  users: [User]!
  post(id: ID!): Post!
  posts: [Post]!
}
